import React, { useState } from "react";

const pastelColors = [
    "linear-gradient(135deg, #ffd8e1, #ffe9f3)",
    "linear-gradient(135deg, #e7f0ff, #d0e4ff)",
    "linear-gradient(135deg, #e6fff7, #ccfcef)",
    "linear-gradient(135deg, #fff3d6, #ffe9c0)"
];

const JournalList = ({ entries, setEntries }) => {
    const [expanded, setExpanded] = useState(null);
    const [editingIndex, setEditingIndex] = useState(null);
    const [editText, setEditText] = useState("");

    const deleteEntry = (i) => {
        const updated = entries.filter((_, idx) => idx !== i);
        setEntries(updated);
        if (expanded === i) setExpanded(null);
    };

    const startEdit = (i) => {
        setEditingIndex(i);
        setEditText(entries[i].text);
    };

    const saveEdit = () => {
        const updated = [...entries];
        updated[editingIndex].text = editText;
        setEntries(updated);
        setEditingIndex(null);
    };

    return (
        <div className="journal-grid">
            {entries.map((entry, i) => (
                <div
                    key={i}
                    className={`journal-card ${expanded === i ? "expanded" : ""}`}
                    style={{ "--pastel": pastelColors[i % pastelColors.length] }}
                    onClick={() => {
                         if (expanded === i) {
                              setExpanded(null);        // collapse on second click 笨能r
                          } else {
                                setExpanded(i);           // open 笨能r
                             }
              }}
                >
                    {/* CRUD buttons */}
                    <div className="card-actions" onClick={(e) => e.stopPropagation()}>
                        <button onClick={() => startEdit(i)}>Edit</button>
                        <button onClick={() => deleteEntry(i)}>Delete</button>
                    </div>

                    {/* If editing */}
                    {editingIndex === i ? (
                        <>
                            <textarea
                                value={editText}
                                onChange={(e) => setEditText(e.target.value)}
                                style={{
                                    width: "100%",
                                    height: "140px",
                                    padding: "10px",
                                    borderRadius: "10px",
                                    boxSizing: "border-box"
                                }}
                                onClick={(e) => e.stopPropagation()}
                            />
                            <div style={{ marginTop: "10px", display: "flex", gap: 8 }}>
                                <button onClick={saveEdit}>Save</button>
                                <button onClick={() => setEditingIndex(null)}>Cancel</button>
                            </div>
                        </>
                    ) : (
                        <>
                            {/* Preview wrapped in reserved space so actions don't overlap */}
                            {expanded !== i && (
                                <div className="card-preview">
                                    <p>
                                        {entry.text.length > 120
                                            ? entry.text.slice(0, 120) + "..."
                                            : entry.text}
                                    </p>
                                </div>
                            )}

                            {/* Full content shown when expanded */}
                            {expanded === i && (
                                <div className="full-content" onClick={(e) => e.stopPropagation()}>
                                    <p>{entry.text}</p>
                                    {entry.image && <img src={entry.image} alt="" />}
                                </div>
                            )}
                        </>
                    )}
                </div>
            ))}
        </div>
    );
};

export default JournalList;
