import React, { useState } from "react";

const pastelColors = [
    "linear-gradient(135deg, #ffd8e1, #ffe9f3)",
    "linear-gradient(135deg, #e7f0ff, #d0e4ff)",
    "linear-gradient(135deg, #e6fff7, #ccfcef)",
    "linear-gradient(135deg, #fff3d6, #ffe9c0)"
];

const JournalList = ({ entries }) => {
    const [openIndex, setOpenIndex] = useState(null);

    const toggle = (i) => {
        setOpenIndex(openIndex === i ? null : i);
    };

    return (
        <div className="journal-grid">
            {entries.map((entry, i) => (
                <div
                    key={i}
                    className={`journal-card ${openIndex === i ? "open" : ""}`}
                    onClick={() => toggle(i)}
                    style={{ "--pastel": pastelColors[i % pastelColors.length] }}
                >
                    <div className="flip-inner">
                        {/* FRONT */}
                        <div className="flip-front" style={{ fontFamily: entry.font }}>
                            {entry.text.length > 60
                                ? entry.text.slice(0, 60) + "..."
                                : entry.text}
                        </div>

                        {/* BACK */}
                        <div className="flip-back" style={{ fontFamily: entry.font }}>
                            <p>{entry.text}</p>
                            {entry.image && <img src={entry.image} alt="" />}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default JournalList;
