import React, { useState } from 'react';

// Random pastel gradients for aesthetic
const pastelGradients = [
    "linear-gradient(135deg, #fef9ff, #ffe4e1)",
    "linear-gradient(135deg, #e0f7fa, #b2ebf2)",
    "linear-gradient(135deg, #fce4ec, #f8bbd0)",
    "linear-gradient(135deg, #fff3e0, #ffe0b2)",
    "linear-gradient(135deg, #f3e5f5, #e1bee7)",
    "linear-gradient(135deg, #e8f5e9, #c8e6c9)",
    "linear-gradient(135deg, #fff0f5, #ffe4ff)",
    "linear-gradient(135deg, #e0f2f1, #b2dfdb)"
];

const JournalList = ({ entries }) => {
    const [flipped, setFlipped] = useState({}); // track flipped cards individually

    const toggleFlip = (index) => {
        setFlipped(prev => ({ ...prev, [index]: !prev[index] }));
    };

    return (
        <div className="journal-grid">
            {entries.map((entry, index) => (
                <div key={index} className="journal-card" onClick={() => toggleFlip(index)}>
                    <div
                        className={`journal-card-inner ${flipped[index] ? 'flip' : ''}`}
                        style={{ background: pastelGradients[index % pastelGradients.length] }}
                    >
                        {/* Front face */}
                        <div className="journal-card-front" style={{ fontFamily: entry.font }}>
                            {entry.text.slice(0, 40)}{entry.text.length > 40 ? '...' : ''}
                        </div>

                        {/* Back face */}
                        <div className="journal-card-back" style={{ fontFamily: entry.font }}>
                            <p style={{ whiteSpace: 'pre-wrap' }}>{entry.text}</p>
                            {entry.image && <img src={entry.image} alt="journal" />}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default JournalList;
