import React, { useState } from "react";

const JournalList = ({ entries }) => {
    const [openCard, setOpenCard] = useState(null);

    const toggleCard = (index) => {
        setOpenCard(openCard === index ? null : index);
    };

    return (
        <div className="journal-grid">
            {entries.map((entry, index) => (
                <div
                    key={index}
                    className={`journal-card ${openCard === index ? "open" : "closed"}`}
                    onClick={() => toggleCard(index)}
                >
                    <div className="journal-header" style={{ fontFamily: entry.font }}>
                        {entry.text.length > 45
                            ? entry.text.slice(0, 45) + "..."
                            : entry.text}
                    </div>

                    {openCard === index && (
                        <div className="journal-content" style={{ fontFamily: entry.font }}>
                            <p>{entry.text}</p>
                            {entry.image && <img src={entry.image} alt="journal content" />}
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default JournalList;
