import React, { useState } from "react";

const pastelColors = [
    "linear-gradient(135deg, #ffd8e1, #ffe9f3)",
    "linear-gradient(135deg, #e7f0ff, #d0e4ff)",
    "linear-gradient(135deg, #e6fff7, #ccfcef)",
    "linear-gradient(135deg, #fff3d6, #ffe9c0)"
];

const JournalList = ({ entries, setEntries }) => {
    const [expanded, setExpanded] = useState(null);
    const [editingIndex, setEditingIndex] = useState(null);
    const [editText, setEditText] = useState("");

    const deleteEntry = (i) => {
        const updated = entries.filter((_, idx) => idx !== i);
        setEntries(updated);
    };

    const startEdit = (i) => {
        setEditingIndex(i);
        setEditText(entries[i].text);
    };

    const saveEdit = () => {
        const updated = [...entries];
        updated[editingIndex].text = editText;
        setEntries(updated);
        setEditingIndex(null);
    };

    return (
        <div className="journal-grid">
            {entries.map((entry, i) => (
                <div
                    key={i}
                    className={`journal-card ${expanded === i ? "expanded" : ""}`}
                    style={{ "--pastel": pastelColors[i % pastelColors.length] }}
                    onClick={() => expanded === i ? setExpanded(null) : setExpanded(i)}
                >
                    {/* CRUD buttons */}
                    <div className="card-actions" onClick={(e) => e.stopPropagation()}>
                        <button onClick={() => startEdit(i)}>Edit</button>
                        <button onClick={() => deleteEntry(i)}>Delete</button>
                    </div>

                    {/* If editing */}
                    {editingIndex === i ? (
                        <>
                            <textarea
                                value={editText}
                                onChange={(e) => setEditText(e.target.value)}
                                style={{
                                    width: "100%",
                                    height: "120px",
                                    padding: "10px",
                                    borderRadius: "10px"
                                }}
                            />
                            <button onClick={saveEdit} style={{ marginTop: "10px" }}>
                                Save
                            </button>
                        </>
                    ) : (
                        <>
                            {/* Preview */}
                            {expanded !== i && (
                                <p>
                                    {entry.text.length > 90
                                        ? entry.text.slice(0, 90) + "..."
                                        : entry.text}
                                </p>
                            )}

                            {/* Full content */}
                            {expanded === i && (
                                <div className="full-content">
                                    <p>{entry.text}</p>
                                    {entry.image && <img src={entry.image} alt="" />}
                                </div>
                            )}
                        </>
                    )}
                </div>
            ))}
        </div>
    );
};

export default JournalList;
