import React, { useState } from 'react';

// Array of pastel colors for cards
const pastelColors = [
    "#ffefeb", "#e0f7fa", "#fce4ec", "#fff3e0",
    "#f3e5f5", "#e8f5e9", "#fff0f5", "#e0f2f1"
];

const JournalList = ({ entries }) => {
    const [openIndex, setOpenIndex] = useState(null);

    const toggleCard = (index) => {
        setOpenIndex(openIndex === index ? null : index);
    };

    return (
        <div className="journal-grid">
            {entries.map((entry, index) => (
                <div
                    key={index}
                    className={`journal-card ${openIndex === index ? 'open' : ''}`}
                    style={{ backgroundColor: pastelColors[index % pastelColors.length] }}
                    onClick={() => toggleCard(index)}
                >
                    <h3 style={{ fontFamily: entry.font }}>
                        {entry.text.slice(0, 30)}{entry.text.length > 30 ? '...' : ''}
                    </h3>
                    <div className="journal-card-content">
                        <p style={{ fontFamily: entry.font, whiteSpace: 'pre-wrap' }}>{entry.text}</p>
                        {entry.image && <img src={entry.image} alt="journal" />}
                    </div>
                </div>
            ))}
        </div>
    );
};

export default JournalList;
