import React from "react";

export default function JournalCard({
  entry,
  index,
  isOpen,
  onToggle,
  onEdit,
  onDelete
}) {
  return (
    <div
      className={`journal-card ${isOpen ? "expanded" : ""}`}
      onClick={() => onToggle(index)}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => (e.key === "Enter" || e.key === " " ? onToggle(index) : null)}
      aria-expanded={isOpen}
    >
      <div className="card-actions" onClick={(e) => e.stopPropagation()}>
        <button className="action-btn" onClick={() => onEdit(index)}>Edit</button>
        <button className="action-btn danger" onClick={() => onDelete(index)}>Delete</button>
      </div>

      <div className="card-preview" style={{ fontFamily: entry.font }}>
        <p>
          {entry.text.length > 120 ? entry.text.slice(0, 120) + "..." : entry.text}
        </p>
      </div>

      {isOpen && (
        <div className="card-full" style={{ fontFamily: entry.font }}>
          <div className="full-text">{entry.text}</div>
          {entry.image && <img src={entry.image} alt="journal" />}
        </div>
      )}
    </div>
  );
}
