import React, { useState } from "react";

const gradients = [
    "linear-gradient(135deg, #fde1ff, #e3c8ff)",
    "linear-gradient(135deg, #c8f4ff, #b6e6ff)",
    "linear-gradient(135deg, #ffe4e1, #ffd1cf)",
    "linear-gradient(135deg, #e8ffe8, #d4ffd4)",
    "linear-gradient(135deg, #fff3cd, #ffe7a3)"
];

const JournalList = ({ entries }) => {
    const [flipped, setFlipped] = useState({});

    const toggle = index => {
        setFlipped(prev => ({ ...prev, [index]: !prev[index] }));
    };

    return (
        <div className="journal-grid">
            {entries.map((entry, index) => (
                <div key={index} className="journal-card" onClick={() => toggle(index)}>
                    <div
                        className={`journal-card-inner ${flipped[index] ? "flip" : ""}`}
                        style={{
                            background: gradients[index % gradients.length]
                        }}
                    >
                        {/* FRONT */}
                        <div className="journal-card-front" style={{ fontFamily: entry.font }}>
                            {entry.text.length > 50 
                                ? entry.text.slice(0, 50) + "..." 
                                : entry.text}
                        </div>

                        {/* BACK */}
                        <div className="journal-card-back" style={{ fontFamily: entry.font }}>
                            <p style={{ whiteSpace: "pre-wrap" }}>{entry.text}</p>

                            {entry.image && (
                                <img src={entry.image} alt="journal" />
                            )}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default JournalList;
